jamoviBAanalysis <- function(
    data,
    method1,
    method2,
    ciDisplay = TRUE,
    ciShading = TRUE,
    titleOfPlot) {

    if ( ! requireNamespace('jmvcore'))
        stop('jamoviBAanalysis requires jmvcore to be installed (restart may be required)')

    options <- jamoviBAanalysisOptions$new(
        method1 = method1,
        method2 = method2,
        ciDisplay = ciDisplay,
